LineComment.Text:
 //First Alarm Detection
IF #SubmoduleAlarms.ESTOP_Detected AND #MachineAlarms.ESTOP_FirstAlarm = '' THEN
    #i := "FC10028_FirstBitActivatedInValue"(#SubmoduleAlarms.ESTOP);
    IF #SubmoduleAlarms.ESTOP_String[#i] = '' AND (LEN(#DBName) < 200) THEN
        #MachineAlarms.ESTOP_FirstAlarm := CONCAT(IN1 := #DBName, IN2 := '.Alarms.ESTOP[', IN3 := DELETE(IN := SINT_TO_STRING(#i), L := 1, P := 1), IN4 := ']');
    ELSIF #SubmoduleAlarms.ESTOP_String[#i] <> '' AND LEN(#SubmoduleAlarms.ESTOP_String[#i]) < 250 THEN
        #MachineAlarms.ESTOP_FirstAlarm := #SubmoduleAlarms.ESTOP_String[#i];
    ELSE
        #MachineAlarms.ESTOP_FirstAlarm := 'Estop Alarm Text Cannot Be Generated';
    END_IF;
END_IF;

